mapper.insert:
insert into gentlepro.club_recruit (id,club_num,act_day,act_time,register_cost,month_cost,type,recruit_position,recruit_due,clubRecruit_detail) values (#{id},#{club_num},#{act_day},#{act_time},#{register_cost},#{month_cost},#{type},#{recruit_position},#{recruit_due},
''
#{clubRecruit_detail}
)
select max(clubRecruit_num) as clubRecruit_num from gentlepro.club_recruit

mapper.select:
select * from (select club_num,clubRecruit_num,act_day,act_time,type,a.id,club_name,club_img,club_color,club_age,club_address,club_locX,club_locY,@rownum := @rownum+1 as rnum from (select * from gentlepro.club_recruit,(select @rownum :=0) rownum where recruit_due>date_sub(now(), interval 1 day)
and type=#{type}
) a join gentlepro.club b using(club_num)) d left outer join (select avg(manner) manner,avg(perform) perform,club_num from gentlepro.club_rating group by club_num)c using(club_num)
where (rnum>#{pageCount} and rnum<=#{pageCount}+30)
