mapper.update:
update gentlepro.club_rating set manner=#{manner},perform=#{perform}

	,rating_detail=#{rating_detail}

where clubrating_num=#{clubrating_num}

mapper.select:
select * from (select *,@rownum := @rownum+1 as rnum from(select recruit_num,c.club_num,recruit_position, recruit_count, recruit_cost,cancel,recruit_detail,type,start_time,end_time,address,match_date,distance,club_name,club_img,club_color,club_age from (select * from (select * from gentlepro.match_recruit,(select @rownum :=0) rownum ) a join (select match_num,cancel,type,start_time,end_time,address,match_date,getDistance(#{club_locY},#{club_locX},address_y,address_x) distance from gentlepro.match where
match_date>date_sub(now(), interval 1 day)
(match_date>=#{start} and match_date<=#{end})
and type=#{type}
) b using(match_num))c join club d using(club_num) order by distance, match_date)z) e left outer join (select avg(manner) manner,avg(perform) perform,club_num,count(*) count from gentlepro.club_rating group by club_num) f using(club_num) where cancel is null and
(rnum>#{pageCount} and rnum<=#{pageCount}+30)

mapper.update:
update gentlepro.match set 
	match_date=#{match_date},
	start_time=#{start_time},
	end_time=#{end_time},
	address=#{address}

	,cost=#{cost}


	,type = #{type}

	,away = #{away}
	,address_x = #{address_x}
	,address_y = #{address_y}
	,away_name = #{away_name}

	,match_detail=#{match_detail}

	where match_num=#{match_num}
